mapper.select:

	select count(*)
	from member
	where id = #{_parameter}

select count(*)
from member
where code = #{_parameter}


	select code, id, name, phone, email, postid, base_deliver, detail_deliver
	from member
	where code = #{_parameter};

mapper.update:
update member set name = #{name},
password=#{password},
postid=#{postid}, base_deliver=#{base_deliver}, detail_deliver=#{detail_deliver}, email = #{email}, phone = #{phone} where id= #{id} and code = #{code}


	update member
	set isdrop = TRUE
	where code = #{code} and password = #{password}
