mapper.select:

	select pi.no,pi.inventory,p.price
	from product_inventory pi inner join product p
	on pi.prd_no = p.no
	where pi.no in (


	
		#{item.prd_no}
	



	)

mapper.insert:
SELECT currval('seq_order');
insert into orders values( nextval('seq_order'), (select concat(to_char(now(),'YYYYMMDD'),'-', (select lpad(cast(count(*)+1 as char) ,7,'0') from orders where to_char(order_date,'YYYY-MM-DD')= to_char(now(),'YYYY-MM-DD'))) ), #{member_code}, #{name}, #{password}, #{deliver}, #{phone}, #{email}, #{rev_name}, #{rev_deliver}, #{rev_phone}, #{deliver_price}, cast(#{status} as enum_order_status), now(), #{pay_way}, #{payment}
)

update.foreach:

	update product_inventory 
	set inventory = inventory - #{item.count}
	where no = #{item.prd_no}
