mapper.insert:

	
		 SELECT currval('seq_product');
	


	insert into product values(
		nextval('seq_product'),
		#{title},
		#{price},
		#{detail},
		now(),
		#{cate_no}
	)


	
		 SELECT currval('seq_prodimg');
	


	insert into product_img(no, prd_no, url, istitle) values


	(nextval('seq_prodimg'),#{prd_no},#{item.url},#{item.istitle})
